controller.up.onEvent(ControllerButtonEvent.Pressed, function () {
    if (Wall == 0) {
        if (GameStart == 1) {
            if (mySprite.vy == 0) {
                mySprite.vy += -95
            }
        }
    } else {
        if (Wall == 1) {
            if (GameStart == 1) {
                if (mySprite.vy == 0) {
                    mySprite.vy += -110
                    mySprite.ay += 180
                    controller.moveSprite(mySprite, 70, 0)
                    characterAnimations.setCharacterAnimationsEnabled(mySprite, true)
                    Wall = 2
                    timer.after(500, function () {
                        Wall = 0
                    })
                }
            }
        }
    }
})
